mapper.update:
update g_categ_busi
set BUSINESS_ID = #{businessId,jdbcType=VARCHAR},
  CATEGORY_ID = #{categoryId,jdbcType=INTEGER},
  PARENT_CATEG_ID = #{parentCategId,jdbcType=INTEGER}
where CATY_BUSI_ID = #{catyBusiId,jdbcType=BIGINT}

mapper.select:
select CATY_BUSI_ID, BUSINESS_ID, CATEGORY_ID, PARENT_CATEG_ID
from g_categ_busi
where CATY_BUSI_ID = #{catyBusiId,jdbcType=BIGINT}
